package core.java.pgms;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name="SD1";
		switch(name) {
		case "LA":
			System.out.println("Welcome to Los Angeles");
			break;
		case "SFO":
			System.out.println("Welcome to San Fransisco ");
			break;
		case "SD":
			System.out.println("Welcome to San Diego ");
			break;
			default:
			System.out.println("Welcome to California");
			
		}

	}

}
